mapper.select:
	/** getList **/
    SELECT
      `cod`.`id`
      ,`cod`.`cd`
      ,`cod`.`name`
      ,`cod`.`ref_id`
      ,`cod`.`group`
    FROM tb_code `cod`
    WHERE
      `cod`.`group` = #{group}

        AND `cod`.`id` = #{id}


        AND `cod`.`cd` = #{cd}


        AND `cod`.`name` = #{name}


        AND `cod`.`ref_id` = #{refId}
