mapper.select:
/** getListOfMonth **/
	SELECT 
		`stb`.`store_id`
		,DATE_FORMAT(`stb`.`bk_st_tms`, "%Y-%m-%d") AS `bk_st_dt`
		,DATE_FORMAT(`stb`.`bk_ed_tms`, "%Y-%m-%d") AS `bk_ed_dt`
		,`stb`.`create_tms`
	FROM
		tb_store_break `stb`
	WHERE
		`stb`.`store_id` = #{storeId}
		AND (
			DATE_FORMAT(`stb`.`bk_st_tms`, "%Y-%m") = #{yearMth}
			OR DATE_FORMAT(`stb`.`bk_ed_tms`, "%Y-%m") = #{yearMth}
		)
